// KCloud.Auth/Controllers/BaseController.csusing Ardalis.Result;using Microsoft.AspNetCore.Mvc;namespace KCloud.Auth.Controllers;[ApiController][Route("api/[controller]")]public abstract class BaseController : ControllerBase{    protected IActionResult HandleResult<T>(Result<T> result)    {        return result.Status switch        {            ResultStatus.Ok => Ok(result.Value),            ResultStatus.Error => BadRequest(result.Errors),            ResultStatus.Unauthorized => Unauthorized(result.Errors),            ResultStatus.NotFound => NotFound(result.Errors),            ResultStatus.Forbidden => Forbid(),            ResultStatus.Invalid => BadRequest(result.ValidationErrors),            _ => StatusCode(500, "An unexpected error occurred")        };    }        protected IActionResult HandleResult(Result result)    {        return result.Status switch        {            ResultStatus.Ok => Ok(),            ResultStatus.Error => BadRequest(result.Errors),            ResultStatus.Unauthorized => Unauthorized(result.Errors),            ResultStatus.NotFound => NotFound(result.Errors),            ResultStatus.Forbidden => Forbid(),            ResultStatus.Invalid => BadRequest(result.ValidationErrors),            _ => StatusCode(500, "An unexpected error occurred")        };    }}