using Ardalis.Result;using KCloud.Auth.Interfaces;using KCloud.Common.Helpers;using MediatR;using Microsoft.Extensions.Localization;namespace KCloud.Auth.Features.Authentication.Commands;public class LogoutCommandHandler(    IRefreshTokenService refreshTokenService,    ILogger<LogoutCommandHandler> logger,    IStringLocalizer localizer,    IHttpContextAccessor httpContextAccessor)    : IRequestHandler<LogoutCommand, Result>{    public async Task<Result> Handle(LogoutCommand request, CancellationToken cancellationToken)    {        try        {            var currentUserId = CurrentUserHelper.GetUserId(httpContextAccessor);            await refreshTokenService.RevokeRefreshTokenAsync(currentUserId ?? Guid.Empty, cancellationToken);                        logger.LogInformation("Successfully logged out user: {UserId}", currentUserId);            return Result.Success();        }        catch (Exception ex)        {            logger.LogError(ex, "Error during logout for user");            return Result.Error(localizer["LogoutError"].Value);        }    }}