// KCloud.Auth/Helpers/CookieHelper.csusing Microsoft.AspNetCore.Http;namespace KCloud.Auth.Helpers;public static class CookieHelper{    public static void SetRefreshTokenCookie(HttpResponse response, string refreshToken, IConfiguration configuration)    {        var cookieOptions = new CookieOptions        {            HttpOnly = true,            Expires = DateTime.UtcNow.AddDays(configuration.GetValue<int>("Jwt:RefreshTokenExpiryDays")),            SameSite = SameSiteMode.Strict,            Secure = true          };                response.Cookies.Append("refreshToken", refreshToken, cookieOptions);    }        public static void RemoveRefreshTokenCookie(HttpResponse response)    {        response.Cookies.Delete("refreshToken");    }}