namespace KCloud.Auth.Helpers;public static class PasswordHasher{    // Hash a password using BCrypt    public static string HashPassword(string password)    {        return BCrypt.Net.BCrypt.HashPassword(password, global::BCrypt.Net.BCrypt.GenerateSalt(12));    }        // Verify a password against a hash    public static bool VerifyPassword(string password, string hashedPassword)    {        return BCrypt.Net.BCrypt.Verify(password, hashedPassword);    }}