namespace KCloud.Auth.Helpers;public static class PasswordHasher{    public static string HashPassword(string password)    {        return BCrypt.Net.BCrypt.HashPassword(password, global::BCrypt.Net.BCrypt.GenerateSalt(12));    }        public static bool VerifyPassword(string password, string hashedPassword)    {        return BCrypt.Net.BCrypt.Verify(password, hashedPassword);    }}