using KCloud.Auth.Interfaces;using KCloud.Auth.Models;using KCloud.Auth.Models.Entity;using Microsoft.EntityFrameworkCore;namespace KCloud.Auth.Helpers;public static class RegisterHelper{    public static async Task<bool> BeUniqueUsername(IUnitOfWork unitOfWork, string username, CancellationToken cancellationToken)    {        var usernameExists = await unitOfWork.Repository<User>()            .Query()            .AnyAsync(u => u.Username.ToLower() == username.ToLower(), cancellationToken);        return !usernameExists;    }        public static async Task<bool> BeUniqueEmail(IUnitOfWork unitOfWork, string email, CancellationToken cancellationToken)    {        var emailExists = await unitOfWork.Repository<User>()            .Query()            .AnyAsync(u => u.Email.ToLower() == email.ToLower(), cancellationToken);        return !emailExists;    }}