namespace KCloud.Auth.Interfaces;public interface IRefreshTokenService{    Task<string> GenerateRefreshTokenAsync(Guid userId, CancellationToken cancellationToken = default);    Task<bool> ValidateRefreshTokenAsync(Guid userId, string token, CancellationToken cancellationToken = default);    Task<bool> RevokeRefreshTokenAsync(Guid userId, CancellationToken cancellationToken = default);}