using AutoMapper;using KCloud.Auth.Models;using KCloud.Auth.Models.DTO;using KCloud.Auth.Models.Entity;namespace KCloud.Auth.Mappings;public class MappingProfile : Profile{    public MappingProfile()    {        CreateMap<User, UserDTO>();                CreateMap<User, AuthResult>()            .ForMember(dest => dest.UserId, opt => opt.MapFrom(src => src.Id))            .ForMember(dest => dest.Token, opt => opt.Ignore())            .ForMember(dest => dest.RefreshToken, opt => opt.Ignore())            .ForMember(dest => dest.Message, opt => opt.Ignore());    }}