// KCloud.Auth/Mappings/MappingProfile.csusing AutoMapper;using KCloud.Auth.Models;using KCloud.Auth.Models.DTO;namespace KCloud.Auth.Mappings;public class MappingProfile : Profile{    public MappingProfile()    {        // Entity to DTO mappings        CreateMap<User, UserDTO>();                // Create AuthResult mapping with custom Message property        CreateMap<User, AuthResult>()            .ForMember(dest => dest.UserId, opt => opt.MapFrom(src => src.Id))            .ForMember(dest => dest.Token, opt => opt.Ignore())            .ForMember(dest => dest.RefreshToken, opt => opt.Ignore())            .ForMember(dest => dest.Message, opt => opt.Ignore());    }}