namespace KCloud.Auth.Models.Entity;public class User{    public Guid Id { get; set; }    public string Username { get; set; } = string.Empty;    public string Email { get; set; } = string.Empty;    public string PasswordHash { get; set; } = string.Empty;    public string Role { get; set; } = "User";        public RefreshToken? RefreshToken { get; set; }}