using System.Globalization;using System.Reflection;using System.Text;using AutoMapper;using FluentValidation;using KCloud.Auth.Data;using KCloud.Auth.Features.Authentication.Commands;using KCloud.Auth.Features.Authentication.Queries;using KCloud.Auth.Interfaces;using KCloud.Auth.Mappings;using KCloud.Auth.Services;using KCloud.Auth.Validators;using Microsoft.AspNetCore.Authentication.JwtBearer;using Microsoft.EntityFrameworkCore;using Microsoft.IdentityModel.Tokens;using Serilog;using KCloud.Common.Resources;using Microsoft.AspNetCore.Localization;using Microsoft.Extensions.Localization;using MediatR;using Ardalis.Result;using KCloud.Common.Interfaces;using KCloud.Common.Services;using Microsoft.AspNetCore.Mvc;Log.Logger = new LoggerConfiguration()    .MinimumLevel.Information()    .WriteTo.Console()    .CreateLogger();try{    Log.Information("Starting KCloud Auth API");    var builder = WebApplication.CreateBuilder(args);        builder.Host.UseSerilog((context, services, configuration) => configuration        .MinimumLevel.Information()        .WriteTo.Console()        .ReadFrom.Configuration(context.Configuration)        .ReadFrom.Services(services));        builder.Services.AddLocalization(options => options.ResourcesPath = "");        builder.Services.AddEndpointsApiExplorer();    builder.Services.AddSwaggerGen();    builder.Services.Configure<RequestLocalizationOptions>(options =>    {        var supportedCultures = new[]        {            new CultureInfo("en-US"),             new CultureInfo("tr-TR")          };            options.DefaultRequestCulture = new RequestCulture("en-US");        options.SupportedCultures = supportedCultures;        options.SupportedUICultures = supportedCultures;    });        builder.Services.AddDbContext<AuthDbContext>(options =>        options.UseSqlite(builder.Configuration.GetConnectionString("DefaultConnection")));        builder.Services.AddScoped<IUnitOfWork, UnitOfWork>();        builder.Services.AddHttpContextAccessor();        builder.Services.AddMediatR(cfg => cfg.RegisterServicesFromAssembly(Assembly.GetExecutingAssembly()));        builder.Services.AddAuthentication(JwtBearerDefaults.AuthenticationScheme)        .AddJwtBearer(options =>        {            options.TokenValidationParameters = new TokenValidationParameters            {                ValidateIssuer = true,                ValidateAudience = true,                ValidateLifetime = true,                ValidateIssuerSigningKey = true,                ValidIssuer = builder.Configuration["Jwt:Issuer"],                ValidAudience = builder.Configuration["Jwt:Audience"],                IssuerSigningKey = new SymmetricSecurityKey(                    Encoding.UTF8.GetBytes(builder.Configuration["Jwt:Key"] ??                        throw new InvalidOperationException("JWT Key is missing in configuration")))            };        });        builder.Services.AddAuthorization();    builder.Services.AddScoped<IValidator<RegisterUserCommand>, RegisterUserCommandValidator>();        var mapperConfig = new MapperConfiguration(cfg =>    {        cfg.AddProfile<MappingProfile>();    });        IMapper mapper = mapperConfig.CreateMapper();        builder.Services.AddSingleton(mapper);        builder.Services.AddScoped<ITokenService, TokenService>();    builder.Services.AddScoped<IRefreshTokenService, RefreshTokenService>();        builder.Services.AddScoped<IResultHandler, ResultHandler>();        builder.Services.AddTransient<IStringLocalizer>(sp =>         sp.GetRequiredService<IStringLocalizerFactory>().Create(typeof(SharedResource)));        var app = builder.Build();    // Configure the HTTP request pipeline    if (app.Environment.IsDevelopment())    {        app.UseSwagger();        app.UseSwaggerUI();    }    app.UseRequestLocalization();        app.UseSerilogRequestLogging();    app.UseHttpsRedirection();    app.UseAuthentication();    app.UseAuthorization();            var authGroup = app.MapGroup("/auth").WithTags("Authentication");        authGroup.MapPost("/register", async (RegisterUserCommand command, IMediator mediator, IResultHandler resultHandler) =>    {        var result = await mediator.Send(command);        return resultHandler.HandleResult(result);    })    .WithName("Register")    .WithSummary("Register a new user");        authGroup.MapPost("/login", async (LoginUserCommand command, IMediator mediator, IResultHandler resultHandler) =>    {        var result = await mediator.Send(command);        return resultHandler.HandleResult(result);    })    .WithName("Login")    .WithSummary("User login");        authGroup.MapPost("/refresh", async (RefreshTokenCommand command, IMediator mediator, IResultHandler resultHandler) =>    {        var result = await mediator.Send(command);        return resultHandler.HandleResult(result);    })    .WithName("RefreshToken")    .WithSummary("Refresh access token");        authGroup.MapPost("/logout", async (LogoutCommand command, IMediator mediator, IResultHandler resultHandler) =>    {        var result = await mediator.Send(command);        return resultHandler.HandleResult(result);    })    .WithName("Logout")    .WithSummary("User logout");        authGroup.MapPost("/me", async (GetCurrentUserQuery query, IMediator mediator, IResultHandler resultHandler) =>    {        var result = await mediator.Send(query);        return resultHandler.HandleResult(result);    })    .WithName("GetCurrentUser")    .WithSummary("Get current user information")    .RequireAuthorization();    var testGroup = app.MapGroup("/test").WithTags("Localization Tests");testGroup.MapGet("/localization", (    [FromServices] IStringLocalizer localizer,    [FromServices] ILogger<Program> logger) =>{    var results = new    {        InvalidPassword = localizer["InvalidPassword"].Value,        InvalidEmail = localizer["InvalidEmail"].Value,        LoginSuccess = localizer["LoginSuccess"].Value,                InvalidPasswordFound = !localizer["InvalidPassword"].ResourceNotFound,        InvalidEmailFound = !localizer["InvalidEmail"].ResourceNotFound,        LoginSuccessFound = !localizer["LoginSuccess"].ResourceNotFound,                AllKeys = localizer.GetAllStrings(includeParentCultures: true)            .Select(x => new { Key = x.Name, Value = x.Value, NotFound = x.ResourceNotFound })            .ToList(),                    CurrentCulture = Thread.CurrentThread.CurrentCulture.Name,        CurrentUICulture = Thread.CurrentThread.CurrentUICulture.Name    };    logger.LogInformation("Localization Test Results: {@Results}", results);    return Results.Ok(results);});testGroup.MapGet("/test-result-handler", (    [FromServices] IResultHandler resultHandler,    [FromServices] ILogger<Program> logger) =>{    try    {        var successResult = Result.Success("Test success message");        var successResponse = resultHandler.HandleResult(successResult);                var errorResult = Result.Error("Test error message");        var errorResponse = resultHandler.HandleResult(errorResult);                logger.LogInformation("ResultHandler test completed successfully");        return Results.Ok(new { Success = "ResultHandler working", SuccessResponse = successResponse, ErrorResponse = errorResponse });    }    catch (Exception ex)    {        logger.LogError(ex, "ResultHandler test failed");        return Results.Problem($"ResultHandler error: {ex.Message}");    }});testGroup.MapPost("/test-login-direct", async (    LoginUserCommand command,     [FromServices] IMediator mediator,    [FromServices] ILogger<Program> logger) =>{    try    {        var result = await mediator.Send(command);                logger.LogInformation("Direct login test - IsSuccess: {IsSuccess}, Errors: {Errors}",             result.IsSuccess, string.Join(", ", result.Errors));                if (result.IsSuccess)        {            return Results.Ok(new { Success = true, Data = result.Value });        }        else        {            return Results.BadRequest(new { Success = false, Errors = result.Errors });        }    }    catch (Exception ex)    {        logger.LogError(ex, "Direct login test failed");        return Results.Problem($"Login error: {ex.Message}");    }});    app.Run();}catch (Exception ex){    Log.Fatal(ex, "Application terminated unexpectedly");}finally{    Log.CloseAndFlush();}