using System.Security.Claims;using Microsoft.AspNetCore.Http;namespace KCloud.Common.Helpers;public static class CurrentUserHelper{    public static Guid? GetUserId(IHttpContextAccessor httpContextAccessor)    {        var user = httpContextAccessor.HttpContext?.User;        if (user != null)        {            // Debug: Log all claims            foreach (var claim in user.Claims)            {                Console.WriteLine($"Claim: {claim.Type} = {claim.Value}");            }        }            var userIdClaim = user?.FindFirstValue(ClaimTypes.NameIdentifier);        return Guid.TryParse(userIdClaim, out var userId) ? userId : null;    }}