using System.Security.Claims;using Microsoft.AspNetCore.Http;namespace KCloud.Common.Helpers;public static class CurrentUserHelper{    public static Guid? GetUserId(IHttpContextAccessor httpContextAccessor)    {        var userIdClaim = httpContextAccessor.HttpContext?.User?.FindFirstValue("userId");        return Guid.TryParse(userIdClaim, out var userId) ? userId : null;    }}