using Ardalis.Result;using FluentValidation;using Microsoft.Extensions.Logging;namespace KCloud.Common.Helpers;public static class ValidationHelper{    public static async Task<Result> ValidateAsync<T>(        IValidator<T> validator,         T model,        ILogger logger)    {        var validationResult = await validator.ValidateAsync(model);        if (!validationResult.IsValid)        {            var errors = validationResult.Errors.Select(e => e.ErrorMessage).ToList();            var errorMessage = string.Join(", ", errors);                    logger.LogWarning("Validation failed: {ValidationErrors}", errorMessage);                        var validationErrors = validationResult.Errors.Select(e => new ValidationError            {                Identifier = e.PropertyName,                ErrorMessage = e.ErrorMessage            }).ToArray();                    return Result.Invalid(validationErrors);        }            return Result.Success();    }    public static bool BeValidName(string name)    {        if (string.IsNullOrEmpty(name))            return false;                char[] invalidChars = { '/', '\\', ':', '*', '?', '"', '<', '>', '|' };        return !name.Any(c => invalidChars.Contains(c));    }}