using Ardalis.Result;using KCloud.FileMetadata.Models.DTO;using MediatR;namespace KCloud.FileMetadata.Features.Files.Queries;public class GetUserFilesQuery : IRequest<Result<UserFilesResponse>>{    public Guid? FolderId { get; set; } // Null = root folder    public bool IncludeSharedFiles { get; set; } = true; // Include files shared with user    public string? SearchTerm { get; set; } // Optional search    public bool IsSharedWithMeFolder { get; set; } }