using Ardalis.Result;using AutoMapper;using FluentValidation;using KCloud.Common.Helpers;using KCloud.FileMetadata.Interfaces;using KCloud.FileMetadata.Models.DTO;using KCloud.FileMetadata.Models.Entity;using MediatR;using Microsoft.EntityFrameworkCore;using Microsoft.Extensions.Localization;namespace KCloud.FileMetadata.Features.Folders.Commands;public class CreateFolderCommandHandler(    IUnitOfWork unitOfWork,    IMapper mapper,    IValidator<CreateFolderCommand> validator,    ILogger<CreateFolderCommandHandler> logger,    IStringLocalizer localizer,    IHttpContextAccessor httpContextAccessor)    : IRequestHandler<CreateFolderCommand, Result<FolderDTO>>{      public async Task<Result<FolderDTO>> Handle(CreateFolderCommand request, CancellationToken cancellationToken)    {        try        {            // Validation errors should return Invalid (400 Bad Request)            var validationResult = await ValidationHelper.ValidateAsync(validator, request, logger);            if (!validationResult.IsSuccess)            {                return Result<FolderDTO>.Invalid(validationResult.ValidationErrors);            }                        var currentUserId = CurrentUserHelper.GetUserId(httpContextAccessor);            if (currentUserId == null)            {                return Result<FolderDTO>.Unauthorized(localizer["Unauthorized"].Value);            }                        // Parent folder validation - NotFound (404) if parent doesn't exist            if (request.ParentFolderId.HasValue)            {                var parentFolder = await unitOfWork.Repository<Folder>().GetByIdAsync(request.ParentFolderId.Value, cancellationToken);                if (parentFolder == null || parentFolder.IsDeleted || parentFolder.UserId != currentUserId.Value)                {                    return Result<FolderDTO>.NotFound(localizer["ParentFolderNotFound"].Value);                }            }                        // Check for duplicate folder names - Conflict (409) for business rule violation            var existingFolder = await unitOfWork.Repository<Folder>()                .Query()                .FirstOrDefaultAsync(f => f.Name == request.Name &&                                          f.UserId == currentUserId.Value &&                                          f.ParentFolderId == request.ParentFolderId &&                                         !f.IsDeleted, cancellationToken);            if (existingFolder != null)            {                logger.LogWarning("Attempted to create duplicate folder '{FolderName}' by user {UserId} in parent {ParentId}",                     request.Name, currentUserId, request.ParentFolderId);                                return Result<FolderDTO>.Conflict(localizer["FolderAlreadyExists"].Value);            }            var folder = new Folder            {                Id = Guid.NewGuid(),                Name = request.Name,                UserId = currentUserId.Value,                ParentFolderId = request.ParentFolderId,                CreatedAt = DateTime.UtcNow,                LastAccessedAt = DateTime.UtcNow,                IsDeleted = false            };            await unitOfWork.Repository<Folder>().AddAsync(folder, cancellationToken);            await unitOfWork.CommitAsync(cancellationToken);            var result = mapper.Map<FolderDTO>(folder);            logger.LogInformation("Folder '{FolderName}' created successfully: {FolderId} by user {UserId}",                 folder.Name, folder.Id, currentUserId);            return Result<FolderDTO>.Success(result);        }        catch (Exception ex)        {            logger.LogError(ex, "Unexpected error creating folder '{FolderName}' for user {UserId}",                 request.Name, CurrentUserHelper.GetUserId(httpContextAccessor));                        // Only true server errors should return Error (500)            return Result<FolderDTO>.Error(localizer["FolderCreationFailed"].Value);        }    }}