namespace KCloud.FileMetadata.Helpers;public static class ExtensionHelper{    public static string? GetExtensionFromContentType(string contentType)    {        return contentType?.ToLowerInvariant() switch        {            "application/pdf" => ".pdf",            "image/jpeg" => ".jpg",            "image/png" => ".png",            "image/gif" => ".gif",            "text/plain" => ".txt",            "application/msword" => ".doc",            "application/vnd.openxmlformats-officedocument.wordprocessingml.document" => ".docx",            "application/vnd.ms-excel" => ".xls",            "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet" => ".xlsx",            _ => null        };    }    public static string GetExtensionFromFileName(string fileName)    {        if (string.IsNullOrEmpty(fileName)) return string.Empty;        var lastDotIndex = fileName.LastIndexOf('.');        return lastDotIndex >= 0 ? fileName.Substring(lastDotIndex) : string.Empty;    }    public static string RemoveExtensionFromFileName(string fileName)    {        if (string.IsNullOrEmpty(fileName)) return fileName;        var lastDotIndex = fileName.LastIndexOf('.');        return lastDotIndex >= 0 ? fileName.Substring(0, lastDotIndex) : fileName;    }}