using AutoMapper;using KCloud.FileMetadata.Models.DTO;using KCloud.FileMetadata.Models.Entity;using KCloud.FileMetadata.Models.Enum;using FileShare = KCloud.FileMetadata.Models.Entity.FileShare;namespace KCloud.FileMetadata.Mappings;public class MappingProfile : Profile{    public MappingProfile()    {        CreateMap<Folder, FolderDetailsDTO>()            .ForMember(dest => dest.FileCount, opt => opt.MapFrom(src => src.Files.Count(f => !f.IsDeleted)))            .ForMember(dest => dest.SubFolderCount, opt => opt.MapFrom(src => src.SubFolders.Count(f => !f.IsDeleted)))            .ForMember(dest => dest.BreadcrumbPath, opt => opt.Ignore())             .ForMember(dest => dest.TotalSizeBytes, opt => opt.Ignore());                CreateMap<Folder, FolderDTO>().ReverseMap();                CreateMap<Models.Entity.FileMetadata, FileMetadataDTO>().ReverseMap();        CreateMap<FileShare, FileShareDTO>().ReverseMap();    }}