using System.ComponentModel.DataAnnotations;namespace KCloud.FileMetadata.Models.Entity;public class FileMetadata{    public Guid Id { get; set; }        [Required]    [MaxLength(75)]    public string FileName { get; set; }        [MaxLength(20)]    public string ContentType { get; set; }        public long FileSizeBytes { get; set; }        [Required]    public Guid UserId { get; set; }         public Guid? FolderId { get; set; }         [MaxLength(500)]    public string StoragePath { get; set; }         public DateTime UploadedAt { get; set; }    public DateTime LastAccessedAt { get; set; }        public bool IsDeleted { get; set; }     public DateTime? DeletedAt { get; set; }        public Folder? Folder { get; set; }    public ICollection<FileShare> Shares { get; set; } = new List<FileShare>();}