// KCloud.FileMetadata/Models/Entity/FileMetadata.csusing System.ComponentModel.DataAnnotations;namespace KCloud.FileMetadata.Models.Entity;public class FileMetadata{    public Guid Id { get; set; }        [Required]    [MaxLength(75)]    public string FileName { get; set; }        [MaxLength(20)]    public string ContentType { get; set; }        public long FileSizeBytes { get; set; }        [Required]    public Guid UserId { get; set; } // Owner - Reference to Auth service user        public Guid? FolderId { get; set; } // Nullable - files can be in root        [MaxLength(500)]    public string StoragePath { get; set; } // Path where actual file is stored        public DateTime UploadedAt { get; set; }    public DateTime LastAccessedAt { get; set; }        public bool IsDeleted { get; set; } // Soft delete    public DateTime? DeletedAt { get; set; }        // Navigation properties    public Folder? Folder { get; set; }    public ICollection<FileShare> Shares { get; set; } = new List<FileShare>();}