using System.ComponentModel.DataAnnotations;using KCloud.FileMetadata.Models.Enum;namespace KCloud.FileMetadata.Models.Entity;public class FileShare{    public Guid Id { get; set; }        [Required]    public Guid FileId { get; set; }        [Required]    public Guid FileOwnerId { get; set; }        [Required]    public Guid SharedByUserId { get; set; }        public Guid? SharedWithUserId { get; set; }        public FilePermission Permission { get; set; }        public DateTime SharedAt { get; set; }        public DateTime? ExpiresAt { get; set; }        public bool IsActive { get; set; } = true;        public bool IsPublic { get; set; } = false;        [MaxLength(16)]    public string? PublicLinkToken { get; set; }        public FileMetadata File { get; set; }}