// KCloud.FileMetadata/Models/Entity/Folder.csusing System.ComponentModel.DataAnnotations;namespace KCloud.FileMetadata.Models.Entity;public class Folder{    public Guid Id { get; set; }        [Required]    [MaxLength(50)]    public string Name { get; set; }        [Required]    public Guid UserId { get; set; }         public Guid? ParentFolderId { get; set; }        public DateTime CreatedAt { get; set; }    public DateTime LastAccessedAt { get; set; }        public bool IsDeleted { get; set; } // Soft delete    public DateTime? DeletedTime { get; set; }        // Navigation properties    public Folder? ParentFolder { get; set; }    public ICollection<Folder> SubFolders { get; set; } = new List<Folder>();    public ICollection<FileMetadata> Files { get; set; } = new List<FileMetadata>();}