using FluentValidation;using KCloud.Common.Helpers;using KCloud.FileMetadata.Features.Folders.Commands;using Microsoft.Extensions.Localization;namespace KCloud.FileMetadata.Validators;public class CreateFolderCommandValidator : AbstractValidator<CreateFolderCommand>{    private readonly IStringLocalizer _localizer;    public CreateFolderCommandValidator(IStringLocalizer localizer)    {        _localizer = localizer;        RuleFor(x => x.Name)            .NotEmpty().WithMessage(_localizer["FolderNameRequired"])            .MaximumLength(75).WithMessage(_localizer["FolderNameMaxLength"])            .Must(ValidationHelper.BeValidName).WithMessage(_localizer["FolderNameInvalidCharacters"]);    }}