using FluentValidation;using KCloud.FileMetadata.Features.Files.Commands;using Microsoft.Extensions.Localization;namespace KCloud.FileMetadata.Validators;public class CreatePublicLinkCommandValidator : AbstractValidator<CreatePublicLinkCommand>{    private readonly IStringLocalizer _localizer;    public CreatePublicLinkCommandValidator(IStringLocalizer localizer)    {        _localizer = localizer;        RuleFor(x => x.FileId)            .NotEmpty().WithMessage(_localizer["FileIdRequired"].Value);        RuleFor(x => x.Permission)            .IsInEnum().WithMessage(_localizer["InvalidPermissionValue"].Value);        RuleFor(x => x.ExpiresAt)            .GreaterThan(DateTime.UtcNow).WithMessage(_localizer["ExpirationDateMustBeFuture"].Value)            .When(x => x.ExpiresAt.HasValue);    }}