// KCloud.FileMetadata/Features/Files/Validators/UpdateFileMetadataCommandValidator.csusing FluentValidation;using KCloud.FileMetadata.Features.Files.Commands;using Microsoft.Extensions.Localization;namespace KCloud.FileMetadata.Validators;public class UpdateFileMetadataCommandValidator : AbstractValidator<UpdateFileMetadataCommand>{    private readonly IStringLocalizer _localizer;    public UpdateFileMetadataCommandValidator(IStringLocalizer localizer)    {        _localizer = localizer;        RuleFor(x => x.Id)            .NotEmpty().WithMessage(_localizer["FileIdRequired"]);        RuleFor(x => x.FileName)            .MaximumLength(75).WithMessage(_localizer["FileNameMaxLength"])            .When(x => !string.IsNullOrEmpty(x.FileName));    }}