using FluentValidation;using KCloud.Common.Helpers;using KCloud.FileMetadata.Features.Folders.Commands;using Microsoft.Extensions.Localization;public class UpdateFolderCommandValidator : AbstractValidator<UpdateFolderCommand>{    private readonly IStringLocalizer _localizer;    public UpdateFolderCommandValidator(IStringLocalizer localizer)    {        _localizer = localizer;        RuleFor(x => x.Id)            .NotEmpty().WithMessage(_localizer["FolderIdRequired"].Value);                       RuleFor(x => x.Name)            .MaximumLength(75).WithMessage(_localizer["FolderNameMaxLength"].Value)            .Must(ValidationHelper.BeValidName).WithMessage(_localizer["FolderNameInvalidCharacters"].Value)            .When(x => !string.IsNullOrWhiteSpace(x.Name));                 RuleFor(x => x)            .Must(x => x.ParentFolderId != x.Id).WithMessage(_localizer["CannotMoveFolderIntoItself"].Value)            .When(x => x.ParentFolderId.HasValue);    }}