// UpdateFolderCommandValidator.csusing FluentValidation;using KCloud.Common.Helpers;using KCloud.FileMetadata.Features.Folders.Commands;using Microsoft.Extensions.Localization;public class UpdateFolderCommandValidator : AbstractValidator<UpdateFolderCommand>{    private readonly IStringLocalizer _localizer;    public UpdateFolderCommandValidator(IStringLocalizer localizer)    {        _localizer = localizer;        RuleFor(x => x.Id)            .NotEmpty().WithMessage(_localizer["FolderIdRequired"]);        // Remove UserId validation - it's set from JWT, not user input                RuleFor(x => x.Name)            .MaximumLength(75).WithMessage(_localizer["FolderNameMaxLength"])            .Must(ValidationHelper.BeValidName).WithMessage(_localizer["FolderNameInvalidCharacters"])            .When(x => !string.IsNullOrWhiteSpace(x.Name)); // Allow null/empty names        // Simple self-reference check        RuleFor(x => x)            .Must(x => x.ParentFolderId != x.Id).WithMessage(_localizer["CannotMoveFolderIntoItself"])            .When(x => x.ParentFolderId.HasValue);    }}