namespace KCloud.FileStorage.Helpers;public static class ContentTypeHelper{    public static string GetContentType(string fileName)    {        var extension = Path.GetExtension(fileName).ToLowerInvariant();        return extension switch        {            ".pdf" => "application/pdf",            ".jpg" or ".jpeg" => "image/jpeg",            ".png" => "image/png",            ".gif" => "image/gif",            ".webp" => "image/webp",            ".txt" => "text/plain",            ".json" => "application/json",            ".doc" => "application/msword",            ".docx" => "application/vnd.openxmlformats-officedocument.wordprocessingml.document",            ".xls" => "application/vnd.ms-excel",            ".xlsx" => "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",            _ => "application/octet-stream"        };    }}