using Microsoft.Extensions.Localization;namespace KCloud.FileStorage.Helpers;public static class FileValidationHelper{    private static readonly string[] AllowedMimeTypes =    {        "image/jpeg", "image/png", "image/gif", "image/webp",        "application/pdf", "text/plain", "application/json",        "application/msword", "application/vnd.openxmlformats-officedocument.wordprocessingml.document",        "application/vnd.ms-excel", "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet"    };    private static readonly string[] AllowedExtensions =    {        ".pdf", ".jpg", ".jpeg", ".png", ".gif", ".webp", ".txt", ".json",        ".doc", ".docx", ".xls", ".xlsx"    };private const long MaxFileSize = 104857600; // 5GB    public static (bool IsValid, string? ErrorMessage) ValidateFile(IFormFile file, IStringLocalizer localizer)    {        if (file == null || file.Length == 0)        {            return (false, localizer["FileRequired"]);        }                if (file.Length > MaxFileSize)        {            return (false, localizer["FileSizeExceedsLimit"]);        }                if (!AllowedMimeTypes.Contains(file.ContentType.ToLower()))        {            return (false, localizer["FileTypeNotAllowed"]);        }                var extension = Path.GetExtension(file.FileName).ToLowerInvariant();        if (!AllowedExtensions.Contains(extension))        {            return (false, localizer["FileExtensionNotAllowed"]);        }        return (true, null);    }    public static string[] GetSupportedExtensions() => AllowedExtensions;}