namespace KCloud.FileStorage.Helpers;public static class StoragePathHelper{    public static string GenerateStoragePath(Guid userId, Guid fileId, string fileName)    {        var fileExtension = Path.GetExtension(fileName);        var guidFileName = $"{fileId}{fileExtension}";        return $"users/{userId}/{guidFileName}";    }    public static string GenerateGuidFileName(Guid fileId, string originalFileName)    {        var fileExtension = Path.GetExtension(originalFileName);        return $"{fileId}{fileExtension}";    }}