using Ardalis.Result;namespace KCloud.FileStorage.Interfaces;public interface IFileStorageService{    Task<Result<string>> SaveFileAsync(IFormFile file, string storagePath, CancellationToken cancellationToken = default);    Task<Result<Stream>> GetFileAsync(string storagePath, CancellationToken cancellationToken = default);    Task<Result> DeleteFileAsync(string storagePath, CancellationToken cancellationToken = default);        Task<Result<string>> FindFileByIdAsync(Guid userId, Guid fileId, CancellationToken cancellationToken = default);}