using Microsoft.AspNetCore.Mvc;namespace KCloud.Gateway;[ApiController][Route("api/[controller]")]public class DebugController : ControllerBase{    private readonly IHttpClientFactory _httpClientFactory;    public DebugController(IHttpClientFactory httpClientFactory)    {        _httpClientFactory = httpClientFactory;    }    [HttpGet("test-filemetadata")]    public async Task<IActionResult> TestFileMetadata()    {        try        {            var client = _httpClientFactory.CreateClient("FileMetadataService");            var response = await client.GetAsync("/api/files");            var content = await response.Content.ReadAsStringAsync();                        return Ok(new {                 StatusCode = response.StatusCode,                Content = content,                Success = response.IsSuccessStatusCode            });        }        catch (Exception ex)        {            return Ok(new { Error = ex.Message });        }    }}