using AutoMapper;using KCloud.Web.Features.Files.Queries;using KCloud.Web.Models.ViewModel;using MediatR;using Microsoft.AspNetCore.Mvc;namespace KCloud.Web.Controllers;public class HomeController(IMediator mediator, IMapper mapper) : BaseController{    public IActionResult Index()    {        return View();    }        public IActionResult About()    {        return View();    }        public IActionResult Pricing()    {        return View();    }       [HttpGet("public/{token}")]    public async Task<IActionResult> AccessPublicFile(string token)    {        if (string.IsNullOrWhiteSpace(token))        {            return RedirectToAction("Index", "Home");        }        var query = new GetFileByPublicLinkQuery(token);        var result = await mediator.Send(query);        if (!result.IsSuccess)        {            TempData["Error"] = string.Join(", ", result.Errors);            return RedirectToAction("Index", "Home");        }        var viewModel = mapper.Map<PublicFileViewModel>(result.Value);        viewModel.PublicToken = token;        return View(viewModel);    }}