using KCloud.Web.Features.Authentication.Commands;using KCloud.Web.Models.DTO;using MediatR;using Microsoft.AspNetCore.Mvc;namespace KCloud.Web.Controllers;[Route("[controller]")]public class UserController(IMediator mediator) : Controller{    [HttpGet("search")]    public async Task<IActionResult> SearchUsers(string term)    {        var query = new SearchUsersQuery(term ?? string.Empty);        var result = await mediator.Send(query);        if (result.IsSuccess)        {            return Json(new { users = result.Value });        }        return Json(new { users = new List<UserSearchResultDTO>() });    }}