using KCloud.Web.Features.Authentication.Commands;using KCloud.Web.Models.DTO;using MediatR;using Microsoft.AspNetCore.Mvc;using Microsoft.AspNetCore.Authorization;namespace KCloud.Web.Controllers;[Route("[controller]")][Authorize]public class UserController(IMediator mediator) : Controller{    [HttpGet("search")]    public async Task<IActionResult> SearchUsers(string term)    {        var query = new SearchUsersQuery(term ?? string.Empty);        var result = await mediator.Send(query);        if (!result.IsSuccess)        {            return Json(new {                 success = false,                message = string.Join(", ", result.Errors),                users = new List<UserSearchResultDTO>()             });        }        return Json(new {             success = true,            users = result.Value         });    }}