using Ardalis.Result;using KCloud.Web.Models.DTO;using MediatR;namespace KCloud.Web.Features.Authentication.Commands;public class RegisterCommand : IRequest<Result<AuthResponseDTO>>{    public RegisterCommand(string email, string password, string username)    {        Email = email;        Password = password;        Username = username;    }    public string Username { get; set; } = string.Empty;    public string Email { get; set; } = string.Empty;    public string Password { get; set; } = string.Empty;}