 using System.Text;using System.Text.Json;using Ardalis.Result;using KCloud.Web.Features.Files.Commands;using KCloud.Web.Interfaces;using KCloud.Web.Models.DTO;using MediatR;namespace KCloud.Web.Features.Files.CommandHandlers;public class CreatePublicLinkCommandHandler(    IHttpClientFactory httpClientFactory,    ILogger<CreatePublicLinkCommandHandler> logger,    IParseServerResponseService parseServerResponseService)    : IRequestHandler<CreatePublicLinkCommand, Result<FileShareDTO>>{    private readonly JsonSerializerOptions _jsonOptions = new()    {        PropertyNameCaseInsensitive = true,        PropertyNamingPolicy = JsonNamingPolicy.CamelCase    };    public async Task<Result<FileShareDTO>> Handle(CreatePublicLinkCommand request, CancellationToken cancellationToken)    {        try        {            var httpClient = httpClientFactory.CreateClient("KCloudGateway");            var json = JsonSerializer.Serialize(request, _jsonOptions);            var content = new StringContent(json, Encoding.UTF8, "application/json");            var response = await httpClient.PostAsync("/file/public-link", content, cancellationToken);            var responseContent = await response.Content.ReadAsStringAsync(cancellationToken);            if (response.IsSuccessStatusCode)            {                logger.LogInformation("Successfully created public link for file {FileId}", request.FileId);                return await parseServerResponseService.ParseSuccessResponse<FileShareDTO>(responseContent);                            }            logger.LogError("Create public link failed with status {StatusCode}: {Response}",                response.StatusCode, responseContent);            var errorResult = await parseServerResponseService.ParseErrorResponse<List<string>>(                responseContent, (int)response.StatusCode);            return Result.Error(string.Join(", ", errorResult.Errors));        }        catch (Exception ex)        {            logger.LogError(ex, "Network error during public link creation for FileId: {FileId}", request.FileId);            return Result.Error("Unable to connect to server");        }    }}