using Ardalis.Result;using Microsoft.AspNetCore.Mvc.ModelBinding;namespace KCloud.Web.Helpers;public class ErrorHelper{    public static void AddToModelState<T>(Result<T> result, ModelStateDictionary modelState)    {        foreach (var error in result.ValidationErrors)        {            modelState.AddModelError(error.Identifier, error.ErrorMessage);        }        foreach (var error in result.Errors)        {            modelState.AddModelError(string.Empty, error);        }    }}