using System.Globalization;using Microsoft.Net.Http.Headers;using StringWithQualityHeaderValue = System.Net.Http.Headers.StringWithQualityHeaderValue;namespace KCloud.Web.Interceptors;public class CultureForwardingHandler : DelegatingHandler{    protected override async Task<HttpResponseMessage> SendAsync(HttpRequestMessage request, CancellationToken cancellationToken)    {        // Use CurrentUICulture (which is what localization sets) instead of CurrentCulture        var currentCulture = CultureInfo.CurrentUICulture.Name;                request.Headers.AcceptLanguage.Clear();        request.Headers.AcceptLanguage.Add(new StringWithQualityHeaderValue(currentCulture));                return await base.SendAsync(request, cancellationToken);    }}