using AutoMapper;using KCloud.Web.Models.DTO;using KCloud.Web.Models.ViewModel;namespace KCloud.Web.Mappings;public class MappingProfile : Profile{    public MappingProfile()    {        CreateMap<UserFilesResponseDTO, FilesIndexViewModel>()            .ForMember(dest => dest.Files, opt => opt.MapFrom(src => src.Files))            .ForMember(dest => dest.Folders, opt => opt.MapFrom(src => src.Folders))            .ForMember(dest => dest.CurrentFolderPath, opt => opt.MapFrom(src => src.CurrentFolderPath))            .ForMember(dest => dest.TotalFiles, opt => opt.MapFrom(src => src.TotalFiles))            .ForMember(dest => dest.TotalFolders, opt => opt.MapFrom(src => src.TotalFolders))            .ForMember(dest => dest.CurrentFolderId, opt => opt.Ignore())             .ForMember(dest => dest.SearchTerm, opt => opt.Ignore())             .ForMember(dest => dest.IsSharedWithMeView, opt => opt.Ignore());     }}