using KCloud.Web.Models.DTO;namespace KCloud.Web.Models.ViewModel;public class FilesIndexViewModel{    public List<FileMetadataDTO> Files { get; set; } = new();    public List<FolderDTO> Folders { get; set; } = new();    public Guid? CurrentFolderId { get; set; }    public string? CurrentFolderPath { get; set; }    public string? SearchTerm { get; set; }    public bool IsSharedWithMeView { get; set; }    public bool IsTrashView { get; set; }    public string OrderBy { get; set; }    public bool Descending { get; set; }    public int TotalFiles { get; set; }    public int TotalFolders { get; set; }    public bool IsRootFolder => !CurrentFolderId.HasValue && !IsSharedWithMeView;    public bool HasItems => Files.Any() || Folders.Any();}