using System.ComponentModel.DataAnnotations;namespace KCloud.Web.Models.ViewModel;public class LoginViewModel{    [Required(ErrorMessage = "EmailRequired")]    [EmailAddress(ErrorMessage = "EmailInvalid")]    [Display(Name = "Email")]    public string Email { get; set; } = string.Empty;    [Required(ErrorMessage = "PasswordRequired")]    [Display(Name = "Password")]    public string Password { get; set; } = string.Empty;}